DROP TABLE IF EXISTS `users`;
DROP TABLE IF EXISTS `customers`;
DROP TABLE IF EXISTS `services`;
DROP TABLE IF EXISTS `products`;
DROP TABLE IF EXISTS `appointments`;
DROP TABLE IF EXISTS `settings`;
CREATE TABLE `users` (`uid` int(100) NOT NULL AUTO_INCREMENT,`username` varchar(100) DEFAULT NULL,`password` varchar(100) DEFAULT NULL,`email` varchar(100) DEFAULT NULL,`phone` int(100) DEFAULT NULL,`ssid` varchar(100) DEFAULT NULL,PRIMARY KEY (`uid`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
